import axios from "axios";
import { useState } from "react";


export const login = async(items) => {

    const customer = await axios.get("http://localhost:8080/customers");

    return {
        c_id: customer.data[0].id,
        c_name: customer.data[items].c_name ,
        c_email: customer.data[0].c_email
    }
};
