import { Box } from "@mui/system";
import { PADDING } from "../constants/Padding";
import AppBarTop from "../components/AppBarTop";
import { Typography } from "@mui/material";
import LogoMangan from "../assets/LogoMangan";
import StyledTextField from "../components/StyledTextField";
import MailOutlineIcon from "@mui/icons-material/MailOutline";
import LockOutlinedIcon from "@mui/icons-material/LockOutlined";
import StyledButton from "../components/StyledButton";
import { useContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { THEME } from '../constants/Theme';
import axios from "axios";
import { Login } from "../components/API/GetAPI";
import { UserContext } from "../UserContext";

export const customer_id = 1;

function LogIn() {
  const [emailInput, setEmailInput] = useState("")
  const [passwordInput, setPasswordInput] = useState("")
  const [buttonColor, setButtonColor] = useState("")
  const [response,setResponse] = useState([])
  const [flag, setFlag]= useState(false)
  const [c_id, setC_id] = useState()
  const navigate = useNavigate()
  const [user, setUser] = useContext(UserContext)



  useEffect(() => {
    btnColor()
  },);

  const btnColor = async () => {
    const response = await axios.get("http://localhost:8080/customers");
    setResponse(response.data)

    if (passwordInput.length >= 8 &&  emailInput.includes("@gmail.com")) {
      setButtonColor(THEME.GREEN_PRIMARY);
      setFlag(true)
    } 

    if(flag === true && (passwordInput.length < 8  || !emailInput.includes("@gmail.com")))
    {
      setButtonColor("");
      setFlag(false)
    }
  };

  const auth = async () => {
    for(let i=0; i<response.length; i++) {
      if(passwordInput === response[i].password && emailInput === response[i].email ) {
        navigate("/home") 
        setC_id(i)
        customer_id = i
      }
    }

    // const data = Login(2)
    setUser({
      c_id: response.data[customer_id].id,
      c_name: response.data[customer_id].name ,
      c_email: response.data[customer_id].email
    })
    console.log(data)
  }

  return (
    <Box
      sx={{
        width: "100%",
        display: "flex",
        flexDirection: "column",
        justifyContent: "flex-start",
        alignItems: "center",
      }}
    >
      <AppBarTop text="Masuk" />

      <Box sx={{ width: PADDING, display: "flex", flexDirection: "column" }}>
        <Box sx={{ mt: "32px" }}>
          <LogoMangan width="60px" height="60px" />
        </Box>
        <Box sx={{ mt: "32px" }}>
          <Typography
            sx={{
              fontSize: "16px",
              fontWeight: 600,
              color: {buttonColor},
              marginBottom: "24px",
            }}
          >
            Masuk Melalui Email
          </Typography>
          <StyledTextField text="Email Address" icon={<MailOutlineIcon />}  onChange={event => {setEmailInput(event.target.value)}}/>
          <StyledTextField
            onChange={event => {setPasswordInput(event.target.value)}}
            text="Password"
            icon={<LockOutlinedIcon />}
            marginTop="16px"
            type="password"
          />
        </Box>
        <StyledButton text="Masuk" marginTop="32px" style="fill" 
        // onClick={() => navigate("/home")}
        btnColorChange={buttonColor}
        onClick={auth}
        />
      </Box>

    </Box>
  );
}

export default LogIn;
