import * as React from 'react';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { THEME } from '../constants/Theme';

export default function BasicSelect({marginTop, label}) {
    const [age, setAge] = React.useState('');

    const handleChange = (event) => {
        setAge(event.target.value);
    };

    return (
        <Box sx={{ minWidth: 120, marginTop: marginTop, backgroundColor: "#FAFAFA", borderRadius: "8px", height: "45px"}}>
            <FormControl fullWidth sx={{height: "45px",}}>
                <InputLabel id="demo-simple-select-label" sx={{
                    fontSize: '16px', color: "#cecece", marginTop: '-5px',
                    '&.Mui-focused': {
                        color: THEME.GREY_PRIMARY
                    }
                }}>{label}</InputLabel>
                <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={age}
                    label={label}
                    onChange={handleChange}
                    sx={{
                        '.MuiOutlinedInput-notchedOutline': {
                            borderColor: "#FAFAFA",
                        },
                        '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                            borderColor: "#FAFAFA",
                        },
                        '&:hover .MuiOutlinedInput-notchedOutline': {
                            borderColor: 'rgba(228, 219, 233, 0.25)',
                        },
                        fontSize: "16px",
                        marginTop: '-5px',
                    }}
                >
                    <MenuItem value={1}>Bandung</MenuItem>
                    <MenuItem value={2}>Besuki</MenuItem>
                    <MenuItem value={3}>Boyolangu</MenuItem>
                    <MenuItem value={4}>Boyolangu</MenuItem>
                </Select>
            </FormControl>
        </Box>
    );
}
