import axios from "axios";
import { useState } from "react";


export const login = async() => {
    const [APICustomer, setAPICustomer] = useContext(AppContext);
    const customer = await axios.get("http://localhost:8080/customers");

    return {
        c_id: customer.data[0].id,
        c_name: customer.data[0].c_name ,
        c_email: customer.data[0].c_email
    }
};
