import * as React from 'react';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { THEME } from '../constants/Theme';
import { Typography } from '@mui/material';
import { useEffect } from 'react';
import axios from 'axios';
import { AppContext } from '../App';
import { useContext } from 'react';
import { useState } from 'react';



export default function BasicSelect({ marginTop, label, icon, type }) {
    const [ value, setValue] = useState('');
    const [idDistrict, setIdDistrict] = useState()
    const [wards, setWards] = useState('')
    const { districtsInTulungagung } = useContext(AppContext)
    

    const handleChange = async(event) => {
        setValue(event.target.value);
        const districts = await axios.get(`https://ibnux.github.io/data-indonesia/kelurahan/${event.target.value}.json`);
        console.log(districts.data)
        setWards(districts.data)
    };

    return (
        <Box sx={{ minWidth: 120, marginTop: marginTop, backgroundColor: "#FAFAFA", borderRadius: "8px", }}>
            <FormControl fullWidth>

                <InputLabel id="demo-simple-select-label" sx={{
                    display: 'flex',

                    '&.Mui-focused': {
                        color: THEME.GREY_PRIMARY
                    }
                }}>

                    {icon}
                    <Typography sx={{ ml: '14px', fontSize: '14px', color: "#9c9c9c", }}>{label}</Typography>

                </InputLabel>
                <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={value}
                    label={label}
                    onChange={handleChange}
                    sx={{
                        '.MuiOutlinedInput-notchedOutline': {
                            borderColor: "#FAFAFA",
                        },
                        '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                            borderColor: "#FAFAFA",
                        },
                        '&:hover .MuiOutlinedInput-notchedOutline': {
                            borderColor: 'rgba(228, 219, 233, 0.25)',
                        },
                        fontSize: "16px"
                    }}
                >
                    {type == "districts" ? 
                    districtsInTulungagung.map((element, index) => (
                            <MenuItem value={districtsInTulungagung[index].id} key={index}>
                                {districtsInTulungagung[index].nama}
                                </MenuItem>
                        )) : ""
                    }
                    {/* {console.log(districtsInTulungagung)} */}
                    {wards? 
                    (type == "wards" ? 
                    // ward.map((element, index) => (
                    //         <MenuItem value={ward[index].id} key={index}>
                    //             {ward[index].nama}
                    //             
                    //             </MenuItem>
                    //     )) 
                    "" : "") : ""
                    
                    }
                    
                    
                  
                </Select>
            </FormControl>
        </Box>
    );
}
