import * as React from 'react';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import { THEME } from '../constants/Theme';
import { Typography } from '@mui/material';
import { useEffect } from 'react';
import axios from 'axios';



export default function BasicSelect({marginTop, label, icon, data}) {
    const [age, setAge] = React.useState('');

    const handleChange = (event) => {   
        setAge(event.target.value);
    };

    return (
        <Box sx={{ minWidth: 120, marginTop: marginTop, backgroundColor: "#FAFAFA", borderRadius: "8px",}}>
            <FormControl fullWidth>
                    
                <InputLabel id="demo-simple-select-label" sx={{
                     display: 'flex',
                    
                    '&.Mui-focused': {
                        color: THEME.GREY_PRIMARY
                    }
                }}>
                    
                    {icon}
                    <Typography sx={{ml: '14px', fontSize: '14px', color: "#9c9c9c",}}>{label}</Typography>
                    
                    </InputLabel>
                <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    value={age}
                    label={label}
                    onChange={handleChange}
                    sx={{
                        '.MuiOutlinedInput-notchedOutline': {
                            borderColor: "#FAFAFA",
                        },
                        '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                            borderColor: "#FAFAFA",
                        },
                        '&:hover .MuiOutlinedInput-notchedOutline': {
                            borderColor: 'rgba(228, 219, 233, 0.25)',
                        },
                        fontSize: "16px"
                    }}
                >
                    {data.map((i)=>(
                        <>
                        <MenuItem value={i}>{data[i].nama}</MenuItem>
                        </>
                    ))}
                    {console.log(data)}
                </Select>
            </FormControl>
        </Box>
    );
}
