import { Box, Typography } from '@mui/material'
import React, { useContext, useEffect, useState } from 'react'
import AppBarTop from '../components/AppBarTop'
import Voucher1 from '../assets/voucher/Voucher1.png'
import Voucher2 from '../assets/voucher/Voucher2.png'
import Voucher3 from '../assets/voucher/Voucher2.png'
import { PADDING } from "../constants/Padding";
import StyledButton from '../components/StyledButton'
import { AppContext } from '../App'
import { voucherModel } from '../components/API/GetAPI'

function AppliedVoucher() {
  const [items, setItems] = useState([]);
  const {
    APIVoucher,
    setAPIVoucher
  } = useContext(AppContext);

  useEffect(() => {
    const items = localStorage.getItem("items");
    if (items) setItems(items);
    getAPI(items);
  }, []);

  const getAPI = async (items) => {
    const vouchers = await voucherModel(items);
    setAPIVoucher(vouchers);
  }

  return (
    <Box
    sx={{
      width: "100%",
      display: "flex",
      flexDirection: "column",
      justifyContent: "flex-start",
      alignItems: "center",
    }}
  >
    <AppBarTop text={"Voucher"} line/>
    {typeof APIFoods == "object"
                  ? Object.keys(APIFoods).map((foods, i) => (
                      <>
                        <Box sx={{ marginRight: "8px" }}>
                          <SelectedMenu
                            title={APIFoods[i].f_name}
                            discount={APIFoods[i].f_discount}
                            price={APIFoods[i].f_price}
                            rating={APIFoods[i].f_rating}
                            sold={APIFoods[i].f_sold}
                            image={APIFoods[i].f_image}
                            index={i}
                            items={items}
                            onClick={() =>
                              navigate(`/product/${APIFoods[i].id}`)
                            }
                          />
                        </Box>
                      </>
                    ))
                  : ""}

        <Box
            sx={{borderRadius: '8px', border: '1px solid #f2f2f2', width: PADDING, marginTop: '25px', overflow: 'hidden'}}
        >
            <img src={Voucher1} alt="voucher" width={"100%"} height={"140px"}/>
            <Box sx={{marginLeft: '13px', marginTop: '10px', marginRight: '13px'}}>
            <Typography sx={{fontSize: '14px', fontWeight: 600}}>Potongan Gratis Ongkir Senilai 15.000</Typography>
            <Box sx={{display: 'flex', justifyContent: 'space-between', mb: '5px'}}>
            <Typography
             sx={{color: '#808080', fontSize: '10px', fontWeight: 500, marginTop: '4px', marginBottom: '15px'}}
            >
            Berlaku sampai 11 Okt 2021
            </Typography>
            <StyledButton width={"30px"} text={"Pakai"} height={"20px"} borderRadius={"3px"} noShadow/>
            </Box>
            </Box>
        </Box>
       
    

  </Box>
  )
}

export default AppliedVoucher
